import React, {Component} from 'react';

import classes from './Toolbar.module.css';

import NavigationItems from '../../components/Navigation/NavigationItems';

class Toolbar extends Component 
    {  

        state = { 
            navigationLinks:[
                {
                    destination:"/aboutUs",
                    linkText: "About Us"
                },
                {
                    destination:"contactUs",
                    linkText:"Get in Touch"
                }
            ]
        }

        updateToolBar(destination){
            let updatedLinks = [...this.state.navigationLinks];

            if(destination == "/aboutUs")
            {
                updatedLinks = [
                    {
                        destination:"/",
                        linkText:"Home"
                    },
                    {
                        destination:"contactUs",
                        linkText:"Get in Touch"
                    }
                ]
            }
            if(destination == "/contactUs")
            {
                updatedLinks = [
                    {
                        destination:"/",
                        linkText:"Home"
                    },
                    {
                        destination:"contactUs",
                        linkText:"Get in Touch"
                    }
                ]
            }
            this.setState({navigationLinks: updatedLinks })
        }

        render(){
            return (      
                <header className = {classes.Toolbar}>
                    <nav className = {classes.DesktopOnly}>
                            <NavigationItems navigationLinks = {this.state.navigationLinks} click = {this.updateToolBar}/>
                    </nav>
                </header> 
            );
        }   
    }

export default Toolbar;
