import React, {Component} from 'react';

import classes from './Toolbar.module.css';

import NavigationItems from '../../components/Navigation/NavigationItems';

import DrawerToggle from '../../components/Navigation/SideDrawer/DrawerToggle/DrawerToggle';

class Toolbar extends Component 
    {  

        state = { 
            navigationLinks:[
                {
                    destination:"/aboutUs",
                    linkText: "About Us"
                },
                {
                    destination:"contactUs",
                    linkText:"Get in Touch"
                }
            ]
        }

        componentDidMount(){
            this.updateToolBar(this.props.pathName);
        }
        componentDidUpdate(prevProps) {
            if(this.props.pathName != prevProps.pathName)
            this.updateToolBar(this.props.pathName);
        }

        updateToolBar = (destination) => {
            let updatedLinks = [...this.state.navigationLinks];
            if(destination == '/'){
                updatedLinks = [
                    {
                        destination:"/aboutUs",
                        linkText: "About Us"
                    },
                    {
                        destination:"contactUs",
                        linkText:"Get in Touch"
                    }
                ] 
            }
            if(destination == "/aboutUs")
            {
                updatedLinks = [
                    {
                        destination:"/",
                        linkText:"Home"
                    },
                    {
                        destination:"/contactUs",
                        linkText:"Get in Touch"
                    }
                ]
            }
            if(destination == "/contactUs")
            {
                updatedLinks = [
                    {
                        destination:"/aboutUs",
                        linkText:"About Us"
                    },
                    {
                        destination:"/",
                        linkText:"Home"
                    }
                ]
            }
            this.setState({navigationLinks: updatedLinks })
        }

        render(){
            return (      
                <header className = {classes.Toolbar}>
                    <DrawerToggle clicked ={props.drawerToggleClicked}/>
                    <nav className = {classes.DesktopOnly}>
                            <NavigationItems navigationLinks = {this.state.navigationLinks}/>
                    </nav>
                </header> 
            );
        }   
    }

export default Toolbar;
