import React, { Component } from 'react';

import Aux from '../../hoc/Aux/Aux';
import errorHandler from '../../hoc/ErrorHandler/ErrorHandler';
import TextAndSquare from '../../components/TextAndSquare/TextAndSquare';
import Countdown from '../../components/Countdown/Countdown';
import { isFulfilled } from 'q';

class Home extends Component {
    // constructor(props) {
    //     super(props);
    //     this.state = {...}
    // }
    state = {
        days: 120,
        hours: 21,
        minutes: 17,
        seconds: 25
    }

    componentDidMount() {

        this.interval = setInterval(() => {

            let seconds = this.state.seconds;
            let updatedSeconds = seconds - 1;
            if ( updatedSeconds == 0) {
                updatedSeconds = 59;
                let minutes = this.state.minutes;
                let updatedMinutes = minutes - 1;
                if(updatedMinutes == 0)
                {
                    updatedMinutes = 60;
                    let hours = this.state.hours;
                    let updatedHours = hours - 1;
                    if(updatedHours == 0){
                        updatedHours = 23;
                        let days = this.state.days;
                        if(days == 0)
                        {
                            console.log("Release day!!");
                            return;
                        }
                        let updatedDays = days - 1;
                    }
                }
            }
            this.setState({ time: Date.now() })
        }, 1000);
      }
      componentWillUnmount() {
        clearInterval(this.interval);
      }

    render () {
        return (
            <Aux>
                <TextAndSquare />
                <Countdown 
                        days = {this.state.days}
                        hours = {this.state.hours}
                        minutes = {this.state.minutes}
                        seconds = {this.state.minutes}/>
            </Aux>
        );
    }
}

export default errorHandler( Home );
